import React, { Component } from 'react';
import { BrowserRouter as Router, Route } from 'react-router-dom'
import './App.css';
import Header from './components/templateLayout/Header.js';
import Footer from './components/templateLayout/Footer.js';
import ViewFlashcardSet from './components/pages/flashcardSets/viewFlashcardSet/ViewFlashcardSet.js';
import EditFlashcardSet from './components/pages/flashcardSets/editFlashcardSet/EditFlashcardSet.js';
import { Link } from 'react-router-dom';
import ViewAsFlashcards from "./components/pages/flashcardSets/viewAsFlashcards/ViewAsFlashcards";

class App extends Component {

    constructor(props) {
        super(props);
        this.state = {
            item:[],
            isLoaded: false
        }
    }

    componentDidMount(){
        fetch('https://jsonplaceholder.typicode.com/users')
            .then(res => res.json())
            .then(json => {
                this.setState({
                    isLoaded: true,
                    items: json,
                })
            });
    }

    render() {
        return (
            <Router>
                <div className="App">
                <div id='container'>
                    <div id='side-left'/>
                    <Header/>
                    <div id='side-right'/>

                    <div id='content'>
                        <Route exact path='/' render={props => (
                            <React.Fragment>
                                <Link to="/view">View</Link>
                                <br/>
                                <br/>
                                <Link to="/edit">Edit</Link>
                            </React.Fragment>
                        )}/>
                        <Route path='/view' component={() => <ViewFlashcardSet/>}/>
                        <Route path='/edit' component={() => <EditFlashcardSet/>}/>
                        <Route path='/study' component={() => <ViewAsFlashcards/>}/>
                    </div>

                    <Footer id='footer'/>
                </div>
                </div>
            </Router>
        );
    }
}

export default App;
