import React, { Component } from 'react';
// import PropTypes from 'prop-types'
import { Link } from 'react-router-dom'

class EditSetHeader extends Component {
    render() {
        return (
            <div style={headerStyle}>
               <textarea style={textareaStyle} value={'Chapter 1: Ten Principles of Economics'}></textarea>
                <p style={privacyStyle}>PRIVATE</p>
                <Link to="/view">
                    <input type='submit' value='Save' style={editStyle}/>
                </Link>
            </div>
        )
    }
}

// PropTypes
// Todos.propTypes = {
//     todos: PropTypes.array.isRequired,
//     markComplete: PropTypes.func.isRequired,
//     delTodo: PropTypes.func.isRequired
// };

const headerStyle = {
    textAlign: 'left',
    height: '50px',
    width: '100%',
    borderBottom: 'solid #bfbfbf 1px',
    display: 'flex',
    // center vertically
    alignItems: 'center',
    flexDirection: 'row',
    justifyContent: 'flex-end'
};
const editStyle = {
    borderRadius: '25px',
    background: '#006298',
    padding: '7px',
    paddingRight: '10px',
    paddingLeft: '10px',
    color: 'white',
    border: 'none',
    marginRight: '10px'
};
const privacyStyle = {
    background: '#006298',
    // padding: '7px',
    // paddingRight: '10px',
    // paddingLeft: '10px',
    color: 'white',
    marginLeft: '4px',
    marginRight: 'auto',
    fontSize: '10px',
    padding: '4px'
};

const textareaStyle = {
    padding: '10px',
    fontFamily: 'Arial',
    fontSize: '16px',
    background: 'none',
    border: 'none',
    resize: 'none',
    outline: 'none',
    overflow: 'hidden',
    color: '#006298',
    width: '320px',
    height: '38px',
};



export default EditSetHeader;
